mapper.insert:
INSERT
  INTO
  		TB_SPRING_BOARD(
  		BOARD_TITLE
  	  , BOARD_CONTENT
  	  , BOARD_WRITER
  	  , CHANGE_NAME
  		)
VALUES
		(
		#{ boardTitle }
	  , #{ boardContent }
	  , #{ boardWriter }
	  , #{ changeName }	
		)

INSERT
  INTO
  		TB_REPLY
VALUES
		(
		SEQ_RNO.NEXTVAL
	  , #{replyWriter}
	  , #{replyContent}
	  , SYSDATE
	  , #{refBoardNo}
		)
